var orderFormInit = function () {
    var orderFormModels = JSON.parse($('#order-form-json').html()).models;
    var orderFormTemplateElement = $('#order-form-template');
    var orderFormTemplate = $($.parseHTML(orderFormTemplateElement.html()));
    var orderFormModel = $('#order-form-model');
    var orderFormModelArray = [];
    orderFormModels.forEach(function (model) {
        orderFormModelArray.push("<option value=\"" + model.id + "\">" + model.name + "</option>");
    });
    orderFormModel.html(orderFormModelArray.join(''));
    orderFormModel.on('change', function (event) {
        var modelId = $(event.currentTarget).val();
        var currentModel = orderFormModels.find(function (model) { return model.id === modelId; });
        var orderFormMetalArray = [];
        var orderFormSideArray = [];
        currentModel.metals.forEach(function (model) {
            orderFormMetalArray.push("<option value=\"" + model.id + "\">" + model.name + "</option>");
        });
        orderFormTemplate.find('#order-form-metal').html(orderFormMetalArray.join(''));
        currentModel.sides.forEach(function (model) {
            orderFormSideArray.push("<option value=\"" + model.id + "\">" + model.name + "</option>");
        });
        orderFormTemplate.find('#order-form-side').html(orderFormSideArray.join(''));
        orderFormTemplate.find('#order-form-price').html(currentModel.price);
        $('#order-form-template').replaceWith(orderFormTemplate);
        $('.order-gallery').attr('hidden', 'hidden');
        $('#order-gallery-' + modelId).removeAttr('hidden');
    }).change();
};
$(function () {
    orderFormInit();
});

